mapper.update:
update facedelect
set filepath = #{filepath,jdbcType=VARCHAR},
  result = #{result,jdbcType=VARCHAR},
  up = #{up,jdbcType=VARCHAR},
  facetime = #{facetime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, filepath, result, up, facetime
from facedelect
where id = #{id,jdbcType=INTEGER}
